import * as React from 'react';
import Login from './comments/Login/Login'
import TopicPage from '../src/comments/TopicPage/TopicPage'
import './App.css';

class App extends React.Component {
  state = {
    appselect: true
  }

  constructor(props){
    super(props)
    this.setLogin = this.setLogin.bind(this)
    this.setTopicPage = this.setTopicPage.bind(this)
  }
  setLogin = () =>{
    this.setState({
      appselect:true
    })
  }

  setTopicPage = () =>{
    this.setState({
      appselect:false
    })
  }

  render(){
    return (
      <div>
        <div className='App-header'>
          {
            this.state.appselect?
            <Login/>
            :''
          }

          {
            !this.state.appselect?
            <TopicPage/>
            :''
          }
        </div>
      </div>
    );
  }

}

export default App;
