import React, { Component } from 'react'

import style from './Topic.module.css'

export default class Topic extends Component {
  state = {
    topicdata:[]
  }

  componentDidMount(){
    this.topicnameTOtopicdata()
  }

  topicnameTOtopicdata = () =>{
    fetch('/topicnameToTopicdata',{
      method: 'post',
      headers:{'Content-Type':'application/json'},
      body:JSON.stringify({
        topicname: this.props.topicmember.topicname
      })
      }).then(response =>response.json()).then(data =>{
        this.setState({
          topicdata: data
        })
      console.log(data)
      })
  }

  render() {
    return (
      <div>
        <div className = {style.topic}>
          <div className = {style.closebutton}><label onClick ={this.props.setPage}>关闭</label></div>
          <div className = {style.topicinfor}>
            <div className = {style.topictitle}>{this.props.topicinfor.topicname}</div>
            <div className ={style.lineintroducion}>
              <div className = {style.topicline}>简介：</div>
              <div className = {style.introduction}>{this.props.topicinfor.introduction}</div>
            </div>
            <div className ={style.disscuss}>
              讨论
            </div>
            <div className = {style.sendmsg}>
              <input className ={style.input}></input><button>发送</button>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
