import * as React from 'react'
import style from './User.module.css'

export default class User extends React.Component{
    state = {
        username:'',
        name:'',
        sex: '',
        phonenumber:'',
        unit:'',
        applylist:[]  
    }

    componentDidMount(){
        this.applylist()
    }

    setName = (e) =>{
        this.setState({
            name: e.target.value
        })
    }

    setSex = (e) =>{
        this.setState({
            sex: e.target.value
        })
    }

    setPhonenumber = (e) =>{
        this.setState({
            phonenumber: e.target.value
        })
    }

    setUnit = (e) =>{
        this.setState({
            unit: e.target.value
        })
    }

    saveUserData = () =>{
        fetch('/saveuserdata',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                username:this.props.userdata.username,
                name:this.state.name,
                sex:this.state.sex,
                phonenumber:this.state.phonenumber,
                unit:this.state.unit
            })
        }).then(response =>response.json()).then(data =>{
            console.log(data)
            window.alert('保存成功')
        })
    }

    applylist = () =>{
        fetch('/applylist',{
            method: 'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                busername: this.props.userdata.username
            })
        }).then(response =>response.json()).then(data =>{
            this.setState({
                applylist:data
            })
            console.log(data)
        })
    }

    applyagree = (topicname,susername,progress) =>{
        return () =>{
            fetch('/applydecide',{
                method: 'post',
                headers:{'Content-Type':'application/json'},
                body:JSON.stringify({
                    topicname: topicname,
                    susername: susername,
                    progress: progress
                })
                }).then(response =>response.json()).then(data =>{
                    
                    console.log(data)
                })
        } 
    }

    applyrefuse = (topicname,susername,progress) =>{
        return () =>{
            fetch('/applydecide',{
                method: 'post',
                headers:{'Content-Type':'application/json'},
                body:JSON.stringify({
                    topicname: topicname,
                    susername: susername,
                    progress: progress
                })
                }).then(response =>response.json()).then(data =>{
                    
                    console.log(data)
                })
        } 
    }

    render(){
        return(
            <div>
                <div  className ={style.backpage}><button onClick = {this.props.setselect}>返回</button></div>
                <div className ={style.userpage}>
                    <div>账号：{this.props.userdata.username}</div> 
                    <div>姓名：<input value={this.props.userdata.name} onChange ={this.setName}></input></div> 
                    <div>性别：<input value={this.props.userdata.sex} onChange ={this.setSex}></input></div> 
                    <div>手机号：<input value={this.props.userdata.phonenumber} onChange ={this.setPhonenumber}></input></div> 
                    <div>单位：<input value={this.props.userdata.unit} onChange ={this.setUnit}></input></div> 
                    <button onClick = {this.saveUserData}>保存</button>
                </div>
                
                <div className = {style.underpage}>   
                    <div className = {style.applypage}>
                        {
                            this.state.applylist.map(item =>{
                                return (
                                <div className = {style.applyinstance} key ={item.toString()}>
                                    <div>{item.topicname}</div>
                                    <div>申请人：{item.susername}</div>
                                    <div>申请理由：{item.resumed}</div>
                                    {
                                        item.progress === '审核中' ?
                                        <div>
                                            <button onClick = {this.applyagree(item.topicname,item.susername,'已同意')}>同意</button>
                                            <button onClick = {this.applyrefuse(item.topicname,item.susername,'已拒绝')}>拒绝</button></div>
                                        :<div>{item.progress}</div>
                                    }
                                </div>
                                )
                            })
                        }
                    </div>

                    <div className = {style.applypage}>

                    </div>
                </div>
            </div>
        );
    }
    
}
