import * as React from 'react';
//import login from '../../server/api/user'
import style from './Login.module.css'

export default class Login extends React.Component {
    state = {
        username: '',
        password: '',
        catigory: 'student'
    }

    usernameChange = (e) =>{
        const username = e.target.value;

        this.setState({
            username: username
        })
    }

    passwordChange = (e) =>{
        const password = e.target.value;

        this.setState({
            password: password
        })
    }

    setStudent = (e) => {
        this.setState ({
            catigory: e.target.name
        })
    }

    setBusiness = (e) =>{
        this.setState ({
            catigory: e.target.name
        })
    }

    login = () =>{
        const username = this.state.username;
        const password = this.state.password;
        if(!username){
            alert('请输入用户名')
            return
        }
        if(!password){
            alert('请输入密码')
            return
        }

        const result = fetch('http://localhost:3000/login',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                username:this.state.username,
                password:this.state.password
            })
        }).then(response=>response.json()).then(data=>{
            //window.alert(data)
            return data
        })
        window.alert(result.username)
        if(result.username === this.state.username){
            
        }
        
        //const result = login(this.state.username,this.state.password);
        // console.log(result)
        // const result = await api('/api/selectF', {
        //     method: 'GET',
        //     body: {
        //         userInfo: {
        //             username: this.state.username, 
        //             password: this.state.password,                 
        //         }
        //     }
        // })

    }

    sign = () =>{
        const username = this.state.username;
        const password = this.state.password;
        if(!username){
            alert('请输入用户名')
            return
        }
        if(!password){
            alert('请输入密码')
            return
        }

    }
    render() {
        return (
            <div>
            <div className={style.login_frame}>
                <div className={style.login_tip}>登录或注册</div>
                <div>
                    <div>
                        <div  className={style.login_aside}>账号：</div>
                            <input type="text" onChange= {this.usernameChange} />
                        <div  className={style.login_aside}>密码：</div>
                            <input type="text" onChange= {this.passwordChange}/> 
                    </div>
                    <input  type='radio' checked = {this.state.catigory === 'student'? 'checked': '' } name='student' onChange = {this.setStudent}/>
                    学生
                    <input  type='radio' checked = {this.state.catigory === 'student'? '': 'checked' } name='business' onChange = {this.setBusiness}/>
                    企业
                    <div>
                        <button  className={style.button_login} onClick ={this.login}>登录</button>
                        <button  className={style.button_sign} onClick ={this.sign}>注册</button>
                    </div> 
                </div>   
            </div>
            </div>
        );
    }
}
