import React, { Component } from 'react'
import style from './Topicrelease.module.css'

export default class Topicrelease extends Component {
    state ={
        topicname: '',
        starttime: '',
        endtime: '',
        introduction: '',
        required: ''
    }

    setTopicname = (e) =>{
        this.setState({
            topicname: e.target.value
        })
    }

    setStarttime = (e) =>{
        this.setState({
            starttime: e.target.value
        })
    }

    setEndtime = (e) =>{
        this.setState({
            endtime: e.target.value
        })
    }

    setIntroduction = (e) =>{
        this.setState({
            introduction: e.target.value
        })
    }

    setRequired = (e) =>{
        this.setState({
            required: e.target.value
        })
    }

    releaseTopic = () =>{

    }

    render() {
        return (
        <div>
            <div>
                <div className = {style.releaseiframe}>
                    <div className = {style.releaseclose}><button onClick = {this.props.setPage}>关闭</button></div>
                    <div className ={style.releaseline}>课题名：<input onChange ={this.setTopicname}/></div>
                    <div className ={style.releaseline}>开始时间：<input onChange ={this.setStarttime}/></div>
                    <div className ={style.releaseline}>结束时间：<input onChange ={this.setEndtime}/></div>
                    <div className ={style.releaseline}>简介：<input onChange ={this.setIntroduction}/></div>
                    <div className ={style.releaseline}>要求：<input onChange ={this.setRequired}/></div>
                    <div className ={style.releaseline}> <button onClick ={this.releaseTopic}> 发布</button> </div>
                </div>
            </div>
        </div>
        )
    }
}
