import React, { Component } from 'react'

import style from './MyTopicList.module.css'

export default class MyTopicList extends Component {
  state = {
    mytopiclist:[],
    topicapply:false,
    applydata:{}
  }

  componentDidMount(){
    this.obtainMyTopiclist()
  }

  obtainMyTopiclist = () =>{
    fetch('/obtaintopiclist',{
      method: 'post',
      headers:{'Content-Type':'application/json'},
      body:JSON.stringify({
        username: this.props.username
      })
      }).then(response =>response.json()).then(data =>{
        this.setState({
          mytopiclist:data
        })
        console.log(data)
      })
  }

  render() {
    return (
      <div>
        <div className ={style.mytitle}>我的课题</div>
        <div className ={style.mytopiclist}>
          {
            this.state.mytopiclist.map((item) => {
              return (<div key = {item.mytopicname} className ={style.mytopic} onClick = {this.setApplydata(item)}>
                      {item.topicname} </div>)
            })
          }
        </div>
      </div>
    )
  }
}
