import React, { Component } from 'react'

import style from './TopicList.module.css'
export default class TopicList extends Component {
  state = {
    topiclist:[],
  }

  componentWillMount(){
    this.obtainTopiclist()
  }

  obtainTopiclist = () =>{
    fetch('/obtaintopiclist',{
      method: 'post',
      header:{'Content-Type':'application/json'},
      body:{}
    }).then(response =>response.json()).then(data =>{
      this.setState({
        topiclist:data
      })
      console.log(data)
    })
  }

  render() {
    return (
      <div>
        <div className ={style.listtitle}>发布的课题</div>
        <div className ={style.topiclist}>
          {
          this.state.topiclist.map((item) => {
              return (<div key = {item.tostring()} className ={style.sigletopic}>item.topicname</div>)
          })
          }
        </div>
      </div>
    )
  }
}
