import React, { Component } from 'react'
import TopicApply from '../TopicApply/TopicApply'
import style from './TopicList.module.css'
export default class TopicList extends Component {
  state = {
    topiclist:[],
  }

  UNSAFE_componentWillMount(){
    this.obtainTopiclist()
  }

  showapply = (item) =>{
    return () => {
        return <TopicApply topicdata = {item}/>
    }
  }

  obtainTopiclist = () =>{
    fetch('/obtaintopiclist',{
      method: 'post',
      header:{'Content-Type':'application/json'},
      body:{}
    }).then(response =>response.json()).then(data =>{
      this.setState({
        topiclist:data
      })
      console.log(data)
    })
  }

  render() {
    return (
      <div>
        <div className ={style.listtitle}>发布的课题</div>
        <div className ={style.topiclist}>
          {
          this.state.topiclist.map((item) => {
            console.log(item.toString())
              return (<div key = {item.topicname} className ={style.sigletopic} onClick = {this.showapply(item)}>
                      {item.topicname} </div>)
          })
          }
        </div>
      </div>
    )
  }
}
