import * as React from 'react'
import style from './User.module.css'

export default class User extends React.Component{
    state = {
        username:'root',
        name:'',
        sex: '',
        phonenumber:'',
        unit:''    
    }

    setName = (e) =>{
        this.setState({
            name: e.target.value
        })
    }

    setSex = (e) =>{
        this.setState({
            sex: e.target.value
        })
    }

    setPhonenumber = (e) =>{
        this.setState({
            phonenumber: e.target.value
        })
    }

    setUnit = (e) =>{
        this.setState({
            unit: e.target.value
        })
    }

    saveUserData = () =>{
        fetch('/saveuserdata',{
            method:'post',
            headers:{'Content-Type':'application/json'},
            body:JSON.stringify({
                username:this.state.username,
                name:this.state.name,
                sex:this.state.sex,
                phonenumber:this.state.phonenumber,
                unit:this.state.unit
            })
        }).then(response =>response.json()).then(data =>{
            console.log(data)
            window.alert('保存成功')
        })
    }

    render(){
        return(
            <div>
                <div  className ={style.backpage}><label onClick = {this.props.setselect}>返回</label></div>
                <div className ={style.userpage}>
                    <div>账号：{this.state.usernane}</div> 
                    <div>姓名：<input value={this.state.nane} onChange ={this.setName}></input></div> 
                    <div>性别：<input value={this.state.sex} onChange ={this.setSex}></input></div> 
                    <div>手机号：<input value={this.state.phonenumber} onChange ={this.setPhonenumber}></input></div> 
                    <div>单位：<input value={this.state.unit} onChange ={this.setUnit}></input></div> 
                    <button onClick = {this.saveUserData}>保存</button>
                </div>
                
            </div>
        );
    }
    
}
