import React, { Component } from 'react'
import NavigationBar from '../NavigationBar/NavigationBar'
import TopicList from '../TopicList/TopicList'
import MyTopicList from '../MyTopicList/MyTopicList'

import User from '../User/User'
import Topic from '../Topic/Topic'
import style from './TopicPage.module.css'

export default class TopicPage extends Component {
  state = {
    nowDisplay: 'topic',

  }

  constructor(props){
    super(props)
    this.setmydata =this.setmydata.bind(this)
    this.setPage =this.setPage.bind(this)
  }

  setmydata = () =>{
    this.setState({
      nowDisplay: 'mydata'
    })
  }

  setPage = () =>{
    this.setState({
      nowDisplay: 'page'
    })
  }

  render() {
    return (
      <div>
          <div>
            <NavigationBar setmydata = {this.setmydata} setLogin = {this.props.setLogin} category = {this.props.userdata.category}/>
            {
              this.state.nowDisplay === 'page' ?
              <div className ={style.topicdiffer}> 
                <TopicList username = {this.props.userdata.username}/>
                <MyTopicList userdata = {this.props.userdata}/>
              </div>
              : ''
            }

            {
              this.state.nowDisplay === 'mydata' ?
              <div>
                <User setselect = {this.setPage} userdata = {this.props.userdata}/>
              </div>
              : ''
            }

            {
              this.state.nowDisplay === 'topic' ?
              <div>
                <Topic setPage = {this.setPage} userdata = {this.props.userdata}/>
              </div>
              : ''
            }
          </div>
      </div>
    )
  }
}
